Example code Executor @Executorpublic class AdvancedTests extends TestBase {private BingController bingController;private WebDriver driver;@BeforeMethodpublic void beforeMethod() {driver = getDriver();bingController = new BingController(driver);}@Test@Description(testIDs = &#34;T-001&#34;, title = &#34;Bing Search&#34;, submodule = &#34;bing&#34;, description = &#34;Search Bing by keywords ${{0}}&#34;)public void testBingSearch(String keywords, String expectedTitle) {String url = &#34;https://bing.com&#34;;bingController.searchAndCheck(url, keywords, expectedTitle);}} Controller @Controllerpublic class BingController extends ControllerBase {private BingSearchPO bingSearchPO;private WebDriver driver;public BingController(WebDriver driver) {this.driver = driver;bingSearchPO = new BingSearchPO(driver);}protected BingController() {}@Step(description = &#34;Search Bing and check result&#34;, retry = 2, intervalWaitInSecond = 3)public void searchAndCheck(String url, String keywords, String expectedTitle) {driver.get(url);bingSearchPO.search(keywords);String result = bingSearchPO.getResultTitle();logger.debug(&#34;Search result title: &#34; + result);Assert.assertTrue(result.equalsIgnoreCase(expectedTitle));}} Operator @Operatorpublic class BingSearchPO extends PageBase {@FindBy(name = &#34;q&#34;)private WebElement searchbox;@FindBy(id = &#34;search_icon&#34;)private WebElement searchButton;public BingSearchPO(WebDriver driver) {super(driver);}protected BingSearchPO() {}@Overridepublic void waitForReady() {}/*** Action: Search Bing by keywords*/@Action(description = &#34;Search Bing by Keywords: ${{0}}&#34;, retry = 2, intervalWaitInSecond = 3)public void search(String keywords) {waitForReadyAndCapturePagePerf();searchbox.clear();searchbox.sendKeys(keywords);Utilities.timeUtility.sleep(2);searchButton.click();}/*** Action: Get result title*/@Action(description = &#34;Get result title&#34;, retry = 2, intervalWaitInSecond = 3)public String getResultTitle() {waitForReadyAndCapturePagePerf();return driver.getTitle();}}
